package ssafy;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.Collections;
import java.util.List;
import java.util.StringTokenizer;

public class case2 {
    private static int testCase, N, K, ans = Integer.MAX_VALUE;
    private static List<Integer> list, select;
    private static boolean[] visited;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        testCase = Integer.parseInt(br.readLine());

        StringTokenizer st;
        StringBuilder sb = new StringBuilder();
        for (int i = 1; i <= testCase; i++) {
            st = new StringTokenizer(br.readLine());
            N = Integer.parseInt(st.nextToken());
            K = Integer.parseInt(st.nextToken());

            visited = new boolean[N];
            list = new ArrayList<>();
            st = new StringTokenizer(br.readLine());
            for (int j = 0; j < N; j++) {
                list.add(Integer.parseInt(st.nextToken()));
            }
            Collections.sort(list);

            ans = Integer.MAX_VALUE;

            for (int start = 0; start <= N - K; start++) {
                select = new ArrayList<>();
                getMinD(start);
            }

            sb.append("#").append(i).append(" ").append(ans);
            if (i != testCase) {
                sb.append("\n");
            }
        }
        System.out.println(sb.toString());
    }

    private static void getMinD(int start) {
        for (int next = start; next < N; next++) {
            if (select.size() == K) {
                int sum = 0;
                for (int i = 0; i < K - 1; i++) {
                    int first = select.get(i);
                    for (int j = i + 1; j < K; j++) {
                        int second = select.get(j);
                        sum += Math.abs(first - second);
                    }
                }
                ans = Math.min(ans, sum);
                return;
            } else {
                select.add(list.get(next));
            }
        }
        if (select.size() == K) {
            int sum = 0;
            for (int i = 0; i < K - 1; i++) {
                int first = select.get(i);
                for (int j = i + 1; j < K; j++) {
                    int second = select.get(j);
                    sum += Math.abs(first - second);
                }
            }
            ans = Math.min(ans, sum);
        }
    }
}

